// Print to console
console.log('Hello, World!');
console.log('Welcome to JavaScript!');

// Basic arithmetic
console.log(2 + 2);        // 4
console.log(10 - 5);       // 5
console.log(3 * 4);        // 12
console.log(20 / 4);       // 5

// Variables
let name = 'Raj';
let age = 25;
let isStudent = true;

console.log('Name:', name);
console.log('Age:', age);
console.log('Is Student:', isStudent);

