// Using let (can be reassigned)
let age = 25;
console.log(age);  // 25

age = 26;  // Reassignment allowed
console.log(age);  // 26

let score;  // Declaration without initialization
score = 100;  // Assignment later

// Using const (cannot be reassigned)
const PI = 3.14159;
const siteName = 'EduLearn India';
const maxUsers = 1000;

console.log(PI);  // 3.14159

// This will cause an error:
// PI = 3.14;  // TypeError: Assignment to constant variable

// const must be initialized
// const x;  // SyntaxError: Missing initializer

// Multiple declarations
let x = 5, y = 10, z = 15;
const a = 1, b = 2, c = 3;

// Destructuring assignment
let [first, second] = [10, 20];
const {name, email} = {name: 'Raj', email: 'raj@example.com'};

