// Global scope
let globalVar = 'I am global';

function myFunction() {
  // Function scope
  let functionVar = 'I am in function';
  console.log(globalVar);  // Accessible
  console.log(functionVar);  // Accessible
}

myFunction();
// console.log(functionVar);  // Error: not defined

// Block scope
if (true) {
  let blockVar = 'I am in block';
  const blockConst = 'Me too';
  console.log(blockVar);  // Accessible
}
// console.log(blockVar);  // Error: not defined

// Good variable names (descriptive)
let userName = 'Priya';
let userAge = 25;
let isLoggedIn = true;
let totalPrice = 999.99;
let itemCount = 5;

// Bad variable names (not descriptive)
let x = 'Priya';  // What is x?
let a = 25;  // What is a?
let flag = true;  // What flag?

// Constants (UPPERCASE)
const MAX_LOGIN_ATTEMPTS = 3;
const API_URL = 'https://api.example.com';
const TAX_RATE = 0.18;

// Boolean variables (use is, has, can)
let isActive = true;
let hasPermission = false;
let canEdit = true;
