// Basic arithmetic
let a = 10;
let b = 3;

console.log(a + b);   // 13 (Addition)
console.log(a - b);   // 7  (Subtraction)
console.log(a * b);   // 30 (Multiplication)
console.log(a / b);   // 3.333... (Division)
console.log(a % b);   // 1  (Modulus - remainder)
console.log(a ** b);  // 1000 (10^3 - Exponentiation)

// Increment and Decrement
let count = 5;
console.log(count++);  // 5 (post-increment: use then increase)
console.log(count);    // 6

let num = 5;
console.log(++num);    // 6 (pre-increment: increase then use)
console.log(num);      // 6

let score = 10;
console.log(score--);  // 10 (post-decrement)
console.log(score);    // 9

// Unary operators
let x = '5';
console.log(+x);       // 5 (converts string to number)
console.log(-x);       // -5 (negates)

// Operator precedence
let result = 2 + 3 * 4;        // 14 (not 20)
let result2 = (2 + 3) * 4;     // 20 (parentheses first)
let result3 = 10 + 5 * 2 ** 3; // 50 (** then * then +)

