// Calculate total price with tax
let price = 1000;
let taxRate = 0.18;  // 18% GST
let tax = price * taxRate;
let total = price + tax;
console.log('Price:', price);
console.log('Tax:', tax);
console.log('Total:', total);  // 1180

// Calculate discount
let originalPrice = 5000;
let discountPercent = 20;
let discount = originalPrice * (discountPercent / 100);
let finalPrice = originalPrice - discount;
console.log('Final Price:', finalPrice);  // 4000

// Calculate average
let marks1 = 85, marks2 = 90, marks3 = 78;
let average = (marks1 + marks2 + marks3) / 3;
console.log('Average:', average.toFixed(2));  // 84.33
