// Object literal (most common)
let person = {
  name: 'Raj Sharma',
  age: 25,
  city: 'Mumbai',
  isStudent: true
};

// Accessing properties
console.log(person.name);   // Raj Sharma (dot notation)
console.log(person['age']); // 25 (bracket notation)

// Adding properties
person.email = 'raj@example.com';
person['phone'] = '9876543210';

// Modifying properties
person.age = 26;
person['city'] = 'Delhi';

// Deleting properties
delete person.isStudent;

// Checking if property exists
console.log('name' in person);    // true
console.log('salary' in person);  // false

// Object with methods
let calculator = {
  add: function(a, b) {
    return a + b;
  },
  subtract: function(a, b) {
    return a - b;
  },
  // Shorthand method syntax
  multiply(a, b) {
    return a * b;
  }
};

console.log(calculator.add(5, 3));       // 8
console.log(calculator.multiply(4, 2));  // 8

// Nested objects
let user = {
  name: 'Priya',
  address: {
    street: 'MG Road',
    city: 'Bangalore',
    pincode: 560001
  },
  hobbies: ['Reading', 'Coding', 'Music']
};

console.log(user.address.city);     // Bangalore
console.log(user.hobbies[0]);       // Reading

