let person = {
  firstName: 'Amit',
  lastName: 'Kumar',
  age: 30,

  // Method using this
  getFullName: function() {
    return this.firstName + ' ' + this.lastName;
  },

  // Arrow function doesn't have own this
  greet: function() {
    console.log(`Hello, I am ${this.firstName}`);
  },

  // Method to modify object
  haveBirthday: function() {
    this.age++;
    console.log(`Happy Birthday! Now ${this.age} years old`);
  }
};

console.log(person.getFullName());  // Amit Kumar
person.greet();                     // Hello, I am Amit
person.haveBirthday();              // Happy Birthday! Now 31 years old
