// Get element by ID
let header = document.getElementById('header');
console.log(header);

// Get elements by class name (returns HTMLCollection)
let items = document.getElementsByClassName('item');
console.log(items[0]);  // First element with class 'item'

// Get elements by tag name
let paragraphs = document.getElementsByTagName('p');
console.log(paragraphs.length);

// Query selector (CSS selector) - returns first match
let firstItem = document.querySelector('.item');
let mainDiv = document.querySelector('#main');
let firstPara = document.querySelector('p');

// Query selector all - returns NodeList
let allItems = document.querySelectorAll('.item');
let allParas = document.querySelectorAll('p');

// Advanced selectors
let nestedElement = document.querySelector('div.container > p.text');
let multipleClasses = document.querySelector('.item.active');
let attributeSelector = document.querySelector('[data-id="123"]');

// Traversing DOM
let parent = element.parentElement;
let children = element.children;
let firstChild = element.firstElementChild;
let lastChild = element.lastElementChild;
let nextSibling = element.nextElementSibling;
let prevSibling = element.previousElementSibling;

