// Template Literals (backticks)
let name = 'Raj';
let age = 25;

// Old way
let message1 = 'My name is ' + name + ' and I am ' + age + ' years old';

// New way (template literal)
let message2 = `My name is ${name} and I am ${age} years old`;

// Multi-line strings
let html = `
  <div>
    <h1>${name}</h1>
    <p>Age: ${age}</p>
  </div>
`;

// Expressions in template literals
let price = 100;
let tax = 18;
let total = `Total: ${price + (price * tax / 100)}`;

// Destructuring Arrays
let colors = ['red', 'green', 'blue'];

// Old way
let color1 = colors[0];
let color2 = colors[1];

// New way (destructuring)
let [first, second, third] = colors;
console.log(first);   // red
console.log(second);  // green

// Skip elements
let [, , thirdColor] = colors;
console.log(thirdColor);  // blue

// Rest in destructuring
let [primary, ...others] = colors;
console.log(primary);  // red
console.log(others);   // ['green', 'blue']

// Destructuring Objects
let person = {
  name: 'Priya',
  age: 22,
  city: 'Mumbai',
  country: 'India'
};

// Old way
let personName = person.name;
let personAge = person.age;

// New way
let { name, age, city } = person;
console.log(name);  // Priya
console.log(age);   // 22

// Rename variables
let { name: userName, age: userAge } = person;

// Default values
let { name, salary = 50000 } = person;
console.log(salary);  // 50000 (default)

// Nested destructuring
let user = {
  id: 1,
  info: {
    name: 'Amit',
    email: 'amit@example.com'
  }
};

let { info: { name, email } } = user;

