// Spread Operator (...) - expands array/object

// Array spread
let arr1 = [1, 2, 3];
let arr2 = [4, 5, 6];
let combined = [...arr1, ...arr2];
console.log(combined);  // [1, 2, 3, 4, 5, 6]

// Copy array
let original = [1, 2, 3];
let copy = [...original];

// Add elements
let numbers = [1, 2, 3];
let moreNumbers = [0, ...numbers, 4, 5];
// [0, 1, 2, 3, 4, 5]

// Object spread
let person = { name: 'Raj', age: 25 };
let employee = { ...person, salary: 50000 };
console.log(employee);
// { name: 'Raj', age: 25, salary: 50000 }

// Merge objects
let obj1 = { a: 1, b: 2 };
let obj2 = { c: 3, d: 4 };
let merged = { ...obj1, ...obj2 };

// Rest Operator - collects remaining elements
function sum(...numbers) {
  return numbers.reduce((total, num) => total + num, 0);
}

console.log(sum(1, 2, 3, 4, 5));  // 15

// Rest in destructuring
let [first, ...rest] = [1, 2, 3, 4, 5];
console.log(first);  // 1
console.log(rest);   // [2, 3, 4, 5]
