// ========== math.js ==========
// Named exports
export function add(a, b) {
  return a + b;
}

export function subtract(a, b) {
  return a - b;
}

export const PI = 3.14159;

// Export multiple at once
function multiply(a, b) {
  return a * b;
}

function divide(a, b) {
  return a / b;
}

export { multiply, divide };

// ========== user.js ==========
// Default export (one per file)
export default class User {
  constructor(name, email) {
    this.name = name;
    this.email = email;
  }

  greet() {
    return `Hello, I'm ${this.name}`;
  }
}

// Can have both default and named exports
export const userRoles = ['admin', 'user', 'guest'];

// ========== app.js ==========
// Import named exports
import { add, subtract, PI } from './math.js';

console.log(add(5, 3));      // 8
console.log(subtract(10, 4)); // 6
console.log(PI);              // 3.14159

// Import all as namespace
import * as MathUtils from './math.js';

console.log(MathUtils.add(2, 3));  // 5
console.log(MathUtils.PI);         // 3.14159

// Import default export
import User from './user.js';

let user = new User('Raj', 'raj@example.com');
console.log(user.greet());

// Import default and named together
import User, { userRoles } from './user.js';

// Rename imports
import { add as sum, subtract as minus } from './math.js';

console.log(sum(5, 3));   // 8
console.log(minus(10, 4)); // 6

