// ========== utils/validation.js ==========
export function validateEmail(email) {
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return regex.test(email);
}

export function validatePhone(phone) {
  const regex = /^[0-9]{10}$/;
  return regex.test(phone);
}

// ========== utils/formatting.js ==========
